import React from 'react';
import {Router, Route, IndexRedirect, hashHistory} from 'react-router';
import {RSTodoAdd, RSTodoAddConfigurator} from '../../views/rs-todo/add';
import {RSTodoList, RSTodoListConfigurator} from '../../views/rs-todo/list';
import {RSWrapper} from '../../views/wrapper';
import RSLayoutCrud from '../../views/layouts/crud';
import RSLayoutSimple from '../../views/layouts/simple';

let RSRoutes;
export default RSRoutes = (
    <Router history={hashHistory}>
        <Route path="/" name="root" component={RSWrapper}>
            <IndexRedirect to="todo"/>
            <Route path="/todo" name="todo" element={RSTodoList}
                   component={RSLayoutCrud}  {...RSTodoListConfigurator.default} />
            <Route path="/todo/add" name="todo.add" element={RSTodoAdd}
                   component={RSLayoutSimple} {...RSTodoAddConfigurator.default} />
        </Route>
    </Router>
)

