import React from 'react';
import {Router, Route, IndexRedirect, hashHistory} from 'react-router';
import {RSMuiTodoAdd, RSMuiTodoAddConfigurator} from '../../views/rs-todo/add';
import {RSMuiTodoList, RSMuiTodoListConfigurator} from '../../views/rs-todo/list';
import {RSMuiWrapper} from '../../views/wrapper';
import RSMuiLayoutCrud from '../../views/layouts/crud';
import RSMuiLayoutSimple from '../../views/layouts/simple';

let RSRoutes;
export default RSRoutes = (
    <Router history={hashHistory}>
        <Route path="/" name="root" component={RSMuiWrapper}>
            <IndexRedirect to="todo"/>
            <Route path="/todo" name="todo" element={RSMuiTodoList}
                   component={RSMuiLayoutCrud}  {...RSMuiTodoListConfigurator.default} />
            <Route path="/todo/add" name="todo.add" element={RSMuiTodoAdd}
                   component={RSMuiLayoutSimple} {...RSMuiTodoAddConfigurator.default} />
        </Route>
    </Router>
)

