'use strict';

module.exports = function (params) {
    var app = params.app;
    var io = params.io;

    var defaultHook = function defaultHook() {
        io.emit('LocalTodo:changed');
    };
    app.models.LocalTodo.observe('after save', function (ctx) {
        defaultHook();
    });

    app.models.LocalTodo.observe('after delete', function () {
        defaultHook();
    });
};