module.exports = (params) => {
    const app = params.app;
    const io = params.io;

    const defaultHook = () => {
        io.emit('LocalTodo:changed');
    }
    app.models.LocalTodo.observe('after save', (ctx) => {
        defaultHook();
    });

    app.models.LocalTodo.observe('after delete', () => {
        defaultHook();
    });
}